  const emojiRegex = /[\u{1F300}-\u{1F9FF}\u{2600}-\u{26FF}\u{2700}-\u{27BF}\u{2B50}\u{2B55}\u{2705}\u{274C}\u{274E}\u{2753}-\u{2757}\u{2795}-\u{2797}\u{2934}\u{2935}\u{25AA}\u{25AB}\u{25B6}\u{25C0}\u{25FB}-\u{25FE}\u{2611}\u{2612}\u{2764}\u{203C}\u{2049}\u{2122}\u{2139}\u{2194}-\u{2199}\u{21A9}\u{21AA}\u{231A}\u{231B}\u{23E9}-\u{23F3}\u{23F8}-\u{23FA}\u{24C2}\u{23CF}]/gu;

  articles.forEach(article => {
    const content = article.content || '';
    const lines = content.split('\n');
    const matches = [];

    lines.forEach((line, idx) => {
