const KEY = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOiIxMjM0NTY3OC0xMjM0LTU2NzgtOTBhYi1jZGVmMTIzNDU2NzgiLCJlbWFpbCI6ImFub25AaW5zZm9yZ2UuY29tIiwicm9sZSI6ImFub24iLCJpYXQiOjE3Njk2OTAzMDN9.ffsGwSQhTIIvoyIUduW1UbG2XV0_WPNQrO_1OuAYASA';
const headers = {
  'Content-Type': 'application/json',
  'apikey': KEY,
  'Authorization': 'Bearer ' + KEY,
  'Prefer': 'return=representation'
};

const star = '<i class=\"fas fa-star\" style=\"color: #f59e0b;\"></i>';
const halfStar = '<i class=\"fas fa-star-half-stroke\" style=\"color: #f59e0b;\"></i>';

fetch(BASE + '/api/database/records/blog_articles', {
  headers: { 'apikey': KEY, 'Authorization': 'Bearer ' + KEY }
}).then(r => r.json()).then(async articles => {

  // === 1. vibe-coding-learning-path-guide ===
  const vibe = articles.find(x => x.id === 'vibe-coding-learning-path-guide');
  if (vibe) {
    let c = vibe.content;
    // æ›¿æ›é †åºå¾ˆé‡è¦ï¼šå…ˆé•·çš„å†çŸ­çš„
    c = c.replaceAll('â­â­â­â­â­', star.repeat(5));
    c = c.replaceAll('â­â­â­â­', star.repeat(4));
    c = c.replaceAll('â­â­â­', star.repeat(3));
    c = c.replaceAll('â­Â½', star + halfStar);
    c = c.replaceAll('â­', star);

    const res = await fetch(BASE + '/api/database/records/blog_articles?id=eq.vibe-coding-learning-path-guide', {
      method: 'PATCH', headers, body: JSON.stringify({ content: c })
    });
    console.log('vibe-coding PATCH:', res.status);
  }

  // === 2. claude-code-ship-zeabur-deploy ===
  const ship = articles.find(x => x.id === 'claude-code-ship-zeabur-deploy');
  if (ship) {
    let c = ship.content;
    c = c.replace('# æ­£ç¢º âœ…', '# æ­£ç¢º');
    c = c.replace('# éŒ¯èª¤ âŒï¼ˆæœƒå›å‚³ SERVICE_NOT_FOUNDï¼‰', '# éŒ¯èª¤ï¼ˆæœƒå›å‚³ SERVICE_NOT_FOUNDï¼‰');

    const res = await fetch(BASE + '/api/database/records/blog_articles?id=eq.claude-code-ship-zeabur-deploy', {
      method: 'PATCH', headers, body: JSON.stringify({ content: c })
    });
    console.log('claude-code-ship PATCH:', res.status);
  }

  // === 3. agent-skill-pipeline ===
  const pipeline = articles.find(x => x.id === 'agent-skill-pipeline');
  if (pipeline) {
