{
  "command": "node -e "
function htmlToMarkdown(html) {
  if (!html) return '';
  if (!/<[^>]+>/.test(html)) return html;
  let markdown = html;
  markdown = markdown.replace(/<br\s*\/?>/gi, '\n');
  markdown = markdown.replace(/<h1[^>]*>(.*?)<\/h1>/gi, '# \$1\n\n');
  markdown = markdown.replace(/<h2[^>]*>(.*?)<\/h2>/gi, '## \$1\n\n');
  markdown = markdown.replace(/<(strong|b)[^>]*>(.*?)<\/\1>/gi, '**\$2**');
  // ä¿ç•™ FA icon
  markdown = markdown.replace(/<(em)[^>]*>(.*?)<\/\1>/gi, '*\$2*');
  markdown = markdown.replace(/<i\s+[^>]*class=\"[^\"]*fa-[^\"]*\"[^>]*><\/i>/gi, (match) => {
    return '<!--FA:' + Buffer.from(match).toString('base64') + '-->';
  });
  markdown = markdown.replace(/<i[^>]*>(.*?)<\/i>/gi, '*\$1*');
  markdown = markdown.replace(/<a[^>]*href=[\"']([^\"']*)[\"'][^>]*>(.*?)<\/a>/gi, '[\$2](\$1)');
  markdown = markdown.replace(/<code[^>]*>(.*?)<\/code>/gi, '\\\`\$1\\\`');
  markdown = markdown.replace(/<hr[^>]*\/?>/gi, '\n---\n\n');
  markdown = markdown.replace(/<p[^>]*>([\s\S]*?)<\/p>/gi, '\$1\n\n');
  markdown = markdown.replace(/<div[^>]*>([\s\S]*?)<\/div>/gi, '\$1\n');
  markdown = markdown.replace(/<[^>]+>/g, '');
  // é‚„åŸ FA icon
  markdown = markdown.replace(/<!--FA:([A-Za-z0-9+\/=]+)-->/g, (_, b64) => {
    return Buffer.from(b64, 'base64').toString('utf8');
  });
  markdown = markdown.replace(/&nbsp;/g, ' ');
  markdown = markdown.replace(/\n{3,}/g, '\n\n');
  return markdown.trim();
}

