/* ========================================
   Custom Scrollbar
   ======================================== */

/* Firefox */
* {
  scrollbar-width: thin;
  scrollbar-color: #525252 transparent;
}

/* WebKit (Chrome, Safari, Edge) */
::-webkit-scrollbar {
  width: 6px;
  height: 6px;
}

::-webkit-scrollbar-track {
  background: transparent;
}

::-webkit-scrollbar-thumb {
  background: #404040;
  border-radius: 3px;
}

::-webkit-scrollbar-thumb:hover {
  background: #525252;
}

::-webkit-scrollbar-corner {
  background: transparent;
}

/* Frontend preview uses light scrollbar */
.frontend-preview::-webkit-scrollbar-thumb {
  background: #d4d4d4;
}

.frontend-preview::-webkit-scrollbar-thumb:hover {
  background: #a3a3a3;
}

.frontend-preview {
  scrollbar-color: #d4d4d4 transparent;
}"
}

