   Chat Window Showcase — Dashboard Dark Scrollbar
   ======================================== */

.chat-window-dark ::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

.chat-window-dark ::-webkit-scrollbar-track {
  background: #000000;
  border-radius: 8px;
}

.chat-window-dark ::-webkit-scrollbar-thumb {
  background: rgba(255, 255, 255, 0.15);
  border-radius: 8px;
  border: 2px solid #000000;
}

.chat-window-dark ::-webkit-scrollbar-thumb:hover {
  background: rgba(255, 255, 255, 0.25);
}

.chat-window-dark ::-webkit-scrollbar-thumb:active {
  background: rgba(255, 255, 255, 0.35);
}

.chat-window-dark * {
  scrollbar-width: thin;
  scrollbar-color: rgba(255, 255, 255, 0.15) #000000;
}

/* ========================================
   Chat Window Showcase — Todo Shimmer Animation
   ======================================== */

@keyframes shimmer {
  0% {
    background-position: -200% 0;
  }
  100% {
    background-position: 200% 0;
  }
}

@keyframes todo-breathe {
  0%, 100% { opacity: 0.4; }
  50% { opacity: 1; }
}

.todo-shimmer {
  background: linear-gradient(
    90deg,
    currentColor 0%,
    currentColor 15%,
    #cccccc 50%,
    currentColor 85%,
    currentColor 100%
  );
  background-size: 200% 100%;
  -webkit-background-clip: text;
  background-clip: text;
  -webkit-text-fill-color: transparent;
  animation: shimmer 3s ease-in-out infinite;
}

.todo-shimmer-dot {
  animation: todo-breathe 3s ease-in-out infinite;
}

