const data = JSON.parse(json);
const post = data.posts.find(p => p.id === 'post-009');
console.log('Post found:', !!post);
console.log('Title:', post.title);
const content = fs.readFileSync(path.join(process.cwd(), post.contentFile), 'utf-8');
console.log('Content starts with:', content.substring(0, 50));
console.log('Content length:', content.length);
console.log('✅ post-009 data + content both load correctly');
"",
