  "file_path": "/Users/ruanbaiye/Documents/Brickverse/ClassPost/app/PostCard.tsx",
  "content": "'use client';

import { useState } from 'react';
import Link from 'next/link';

function formatId(id: string) {
  // "post-012" → "Post012"
  return id.replace(/^post-/, 'Post');
}

interface PostCardProps {
  id: string;
  title: string;
  subtitle: string;
  index: number;
  format?: string;
  duration?: string;
}

export default function PostCard({ id, title, subtitle, index, format, duration }: PostCardProps) {
  const [copied, setCopied] = useState(false);
  const displayId = formatId(id);

  const handleCopyId = async (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    try {
      await navigator.clipboard.writeText(displayId);
    } catch {
      const textarea = document.createElement('textarea');
      textarea.value = displayId;
      textarea.style.position = 'fixed';
      textarea.style.opacity = '0';
      document.body.appendChild(textarea);
      textarea.select();
      document.execCommand('copy');
      document.body.removeChild(textarea);
    }
    setCopied(true);
    setTimeout(() => setCopied(false), 1500);
  };

  return (
    <Link
      href={`/post/${id}`}
      style={{ textDecoration: 'none', color: 'inherit' }}
    >
      <article className="post-card">
        <div className="post-card-header">
          <span className="post-index">#{index}</span>
          <button
            className={`post-id-badge${copied ? ' copied' : ''}`}
            onClick={handleCopyId}
            title="複製 ID"
          >
            {copied ? '已複製' : displayId}
          </button>
        </div>
        <h2 className="post-title">{title}</h2>
        <p className="post-subtitle">{subtitle}</p>
        {(format || duration) && (
          <p className="post-meta">
            {format || ''}
            {format && duration ? ' · ' : ''}
            {duration || ''}
          </p>
        )}
      </article>
    </Link>
  );
}
"
}

