{
  "file_path": "/Users/ruanbaiye/Documents/UtilityTools/ClaudeCodeLab/src/components/OfficialCoursePage.tsx",
  "content": "'use client';

interface OfficialCoursePageProps {
  onBackToHome: () => void;
}

const courseOutline = [
  {
    part: 1,
    titleEn: 'Fundamentals',
    titleZh: '基礎概念',
    lessons: ['What is a Coding Assistant?', 'Setup and Initial Configuration'],
  },
  {
    part: 2,
    titleEn: 'Context Management',
    titleZh: '上下文管理',
    lessons: [
      'What is CLAUDE.md?',
      'CLAUDE.md Locations and Loading Order',
      'Writing Effective CLAUDE.md Files',
      'Adding Memory with the # Shortcut',
    ],
  },
  {
    part: 3,
    titleEn: 'Custom Commands',
    titleZh: '自訂指令',
    lessons: ['Creating Custom Slash Commands', 'Practical Custom Command Examples'],
  },
  {
    part: 4,
    titleEn: 'MCP Server Integration',
    titleZh: 'MCP 伺服器整合',
    lessons: [
      'What is MCP?',
      'Configuring MCP Servers',
      'Practical MCP Server Examples',
      "What's Possible with MCP Servers Connected",
    ],
  },
  {
    part: 5,
    titleEn: 'Hooks',
    titleZh: '鉤子（Hooks）',
    lessons: ['What are Hooks?', 'Hook Event Types', 'Practical Hook Configuration Examples'],
  },
  {
    part: 6,
    titleEn: 'GitHub Integration',
    titleZh: 'GitHub 整合',
    lessons: ['Setting Up GitHub Integration', 'GitHub Actions Integration'],
  },
  {
    part: 7,
    titleEn: 'Claude Code SDK',
    titleZh: 'Claude Code SDK',
    lessons: ['SDK Overview'],
  },
];

export default function OfficialCoursePage({ onBackToHome }: OfficialCoursePageProps) {
  return (
    <div className="min-h-screen bg-gray-950 text-white">
      {/* Header */}
      <header className="border-b border-gray-800 px-6 py-4">
        <div className="container mx-auto max-w-4xl flex items-center">
          <button
            onClick={onBackToHome}
            className="flex items-center gap-2 text-gray-400 hover:text-white hover:bg-gray-800 rounded-lg px-3 py-1.5 transition-colors text-sm"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
            </svg>
            回到首頁
          </button>
        </div>
      </header>

      <main className="container mx-auto px-6 py-8 max-w-4xl">
        <h1 className="text-2xl font-bold mb-4">官方課程：Claude Code in Action</h1>
        <p className="text-gray-300 leading-relaxed mb-6">
          這是 Anthropic 官方推出的 Claude Code 課程，涵蓋從基礎設定到 SDK 整合的完整內容。
          共 7 個章節、約 18 堂課，適合想深入了解 Claude Code 各項功能的使用者。
        </p>

        <a
          href="https://anthropic.skilljar.com/claude-code-in-action"
          target="_blank"
          rel="noopener noreferrer"
          className="inline-flex items-center gap-2 bg-violet-500 hover:bg-violet-600 text-white font-bold py-3 px-6 rounded-xl transition-colors mb-10"
        >
          前往官方課程
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
          </svg>
        </a>

        {/* Course Outline */}
        <h2 className="text-xl font-bold mb-6">課程大綱</h2>
        <div className="grid gap-4">
          {courseOutline.map((section) => (
            <div
              key={section.part}
              className="border border-gray-800 rounded-xl p-6"
            >
              <div className="flex items-center gap-3 mb-3">
                <span className="bg-violet-500/20 text-violet-400 text-xs font-bold px-2.5 py-1 rounded-full">
                  Part {section.part}
                </span>
                <h3 className="font-bold">
                  {section.titleEn}
                  <span className="text-gray-400 font-normal ml-2">
                    {section.titleZh}
                  </span>
                </h3>
              </div>
              <ul className="space-y-1.5 ml-1">
                {section.lessons.map((lesson, i) => (
                  <li key={i} className="flex items-start gap-2 text-sm text-gray-400">
                    <span className="text-gray-600 mt-0.5 shrink-0">&#8226;</span>
                    {lesson}
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}
"
}

