  "command": "npx next dev --port 3011 &",
  "description": "Start dev server in background",
  "run_in_background": true
}

{
  "command": "sleep 8 && curl -s -o /dev/null -w \"%{http_code}\" http://localhost:3011",
  "description": "Check if dev server responds",
  "timeout": 20000
}

{
  "command": "sleep 10 && curl -s -o /dev/null -w \"%{http_code}\" http://localhost:3011",
  "description": "Retry dev server check after more wait",
