  "command": "npx next dev --port 3011 2>&1 &\nPID=$!\nsleep 15\ncurl -s -o /dev/null -w \"%{http_code}\" http://localhost:3011\nkill $PID 2>/dev/null",
  "description": "Start dev server, wait, check, then stop",
  "timeout": 60000
}

{
  "command": "ps aux | grep \"next dev\" | grep -v grep",
