                <div className="grid gap-3">
                  {partUnits.map((unit) => {
                    const total = unit.exercises.length;
                    const completed = getCompletedCount(unit.exercises, progress);

                    return (
                      <button
                        key={unit.id}
                        onClick={() => onSelectUnit(unit.id)}
                        className="border border-gray-800 rounded-xl p-5 hover:border-gray-600 transition-colors text-left group"
                      >
                        <div className="flex justify-between items-start mb-2">
                          <div>
                            <h5 className="font-bold group-hover:text-violet-400 transition-colors">
                              單元 {unit.id}：{unit.title}
                            </h5>
                            <p className="text-gray-400 text-sm mt-1">
                              {unit.description}
                            </p>
                          </div>
                          <span className="text-sm text-gray-500 shrink-0 ml-4">
                            {completed}/{total} 已完成
                          </span>
                        </div>
                        <div className="w-full bg-gray-800 rounded-full h-1.5">
                          <div
                            className="bg-violet-400 h-1.5 rounded-full transition-all"
                            style={{ width: `${total > 0 ? (completed / total) * 100 : 0}%` }}
                          />
                        </div>
                      </button>
                    );
                  })}
                </div>
              </div>
            );
          })}
        </div>
      </section>"
}

