export type ExerciseType = 'multiple-choice';

// ===== 多選題 =====
export interface MultipleChoiceOption {
  id: string;
  label: string;
  explanation?: string;
}

export interface MultipleChoiceData {
  options: MultipleChoiceOption[];
  correctIds: string[];
  allowMultiple: boolean;
}

// ===== 練習 =====
export interface Exercise {
  id: string;
  title: string;
  description: string;
  requirements: string[];
  hints: string[];
  type: ExerciseType;
  multipleChoiceData?: MultipleChoiceData;
}

// ===== 閱讀內容 =====
export interface ConceptBlock {
  title: string;
  content: string;
  codeExample?: string;
  codeLanguage?: string;
}

export interface UnitReadingContent {
  introduction: string;
  concepts: ConceptBlock[];
  tips: string[];
}

// ===== 單元 =====
export interface Unit {
  id: number;
  title: string;
  description: string;
  readingContent: UnitReadingContent;
  exercises: Exercise[];
  completionText?: string;
}

// ===== 驗證結果 =====
export interface VerificationResult {
  allPassed: boolean;
  correctCount?: number;
  totalCount?: number;
  details?: { id: string; correct: boolean; feedback?: string }[];
}

// ===== 進度 =====
export interface ExerciseProgress {
  [exerciseId: string]: {
    completed: boolean;
    userCode?: string;
    userAnswer?: unknown;
  };
}
