  "content": "import type {
  VerificationResult,
  MultipleChoiceData,
} from '@/types/exercise';

// ===== Multiple Choice 驗證 =====

export function verifyMultipleChoice(
  selectedIds: string[],
  data: MultipleChoiceData
): VerificationResult {
  const sortedSelected = [...selectedIds].sort();
  const sortedCorrect = [...data.correctIds].sort();
  const allPassed =
    sortedSelected.length === sortedCorrect.length &&
    sortedSelected.every((id, i) => id === sortedCorrect[i]);

  const details = data.options.map((opt) => {
    const isCorrect = data.correctIds.includes(opt.id);
    const isSelected = selectedIds.includes(opt.id);
    return {
      id: opt.id,
      correct: isCorrect === isSelected,
      feedback: opt.explanation,
    };
  });

  return {
    allPassed,
    correctCount: details.filter((d) => d.correct).length,
    totalCount: details.length,
    details,
  };
}
"
}

