  "content": "'use client';

import { useState, useCallback } from 'react';
import type {
  VerificationResult,
  MultipleChoiceData,
} from '@/types/exercise';
import { verifyMultipleChoice } from '@/lib/validators';

export function useVerification() {
  const [verificationResult, setVerificationResult] = useState<VerificationResult | null>(null);
  const [isVerifying, setIsVerifying] = useState(false);

  const verifyChoice = useCallback(
    async (selectedIds: string[], data: MultipleChoiceData): Promise<VerificationResult> => {
      setIsVerifying(true);
      setVerificationResult(null);
      await new Promise((r) => setTimeout(r, 300));
      const result = verifyMultipleChoice(selectedIds, data);
      setVerificationResult(result);
      setIsVerifying(false);
      return result;
    },
    []
  );

  const resetVerification = useCallback(() => {
    setVerificationResult(null);
    setIsVerifying(false);
  }, []);

  return {
    verificationResult,
    isVerifying,
    verifyChoice,
    resetVerification,
  };
}
"
}

