export default function ExerciseWorkspace({
  unit,
  exercise,
  progress,
  verificationResult,
  isVerifying,
  onReset,
  onSelectExercise,
  onBackToUnits,
  onSubmitChoice,
}: ExerciseWorkspaceProps) {
  return (
    <div className="flex-1 grid min-h-0 grid-cols-[220px_1fr_1.5fr]">
      {/* Col 1: Sidebar */}
      <div className="border-r border-gray-800 bg-gray-900/30 overflow-y-auto">
        <ExerciseSidebar
          unit={unit}
          currentExerciseId={exercise.id}
          progress={progress}
          onSelectExercise={onSelectExercise}
          onBackToUnits={onBackToUnits}
        />
      </div>

      {/* Col 2: Exercise description */}
      <div className="border-r border-gray-800 p-6 overflow-y-auto">
        <ExercisePanel
          exercise={exercise}
          isCompleted={progress[exercise.id]?.completed ?? false}
          onReset={onReset}
        />
      </div>

      {/* Col 3: Multiple choice exercise + verification */}
      <div className="flex flex-col min-h-0 overflow-y-auto">
        {exercise.multipleChoiceData && (
          <MultipleChoiceExercise
            data={exercise.multipleChoiceData}
            exerciseId={exercise.id}
            result={verificationResult}
            onSubmit={onSubmitChoice}
            isVerifying={isVerifying}
          />
        )}
        <VerificationPanel result={verificationResult} isVerifying={isVerifying} />
      </div>
    </div>
  );
}
