  "content": "'use client';

import { useState, useEffect, useCallback } from 'react';
import { units, getUnit, getUnitForExercise } from '@/data/exercises';
import { loadProgress, saveProgress, markCompleted } from '@/lib/progress';
import { useVerification } from '@/hooks/useVerification';
import type { ExerciseProgress } from '@/types/exercise';
import LandingPage from '@/components/LandingPage';
import UnitDetailPage from '@/components/UnitDetailPage';
import PrefacePage from '@/components/PrefacePage';
import OfficialCoursePage from '@/components/OfficialCoursePage';
import ExerciseNav from '@/components/ExerciseNav';
import ExerciseWorkspace from '@/components/ExerciseWorkspace';

type ViewState = 'home' | 'preface' | 'official-course' | 'unit' | 'exercise';

export default function Home() {
  const [view, setView] = useState<ViewState>('home');
  const [selectedUnitId, setSelectedUnitId] = useState<number | null>(null);
  const [selectedExerciseId, setSelectedExerciseId] = useState<string | null>(null);
  const [progress, setProgress] = useState<ExerciseProgress>({});

  const {
    verificationResult,
    isVerifying,
    verifyChoice,
    resetVerification,
  } = useVerification();

  // Load progress
  useEffect(() => {
    setProgress(loadProgress());
  }, []);

  // Derived state
  const currentUnit = selectedExerciseId ? getUnitForExercise(selectedExerciseId) : null;
  const currentExercise = currentUnit?.exercises.find((e) => e.id === selectedExerciseId);
  const currentIndex = currentUnit?.exercises.findIndex((e) => e.id === selectedExerciseId) ?? -1;

  // Reset verification when exercise changes
  useEffect(() => {
    if (currentExercise) {
      resetVerification();
    }
  }, [selectedExerciseId, currentExercise, resetVerification]);

  // Redirect invalid state
  useEffect(() => {
    if (view === 'unit' && selectedUnitId !== null && !getUnit(selectedUnitId)) {
      setView('home');
    }
    if (view === 'exercise' && (!currentUnit || !currentExercise)) {
      setView('home');
    }
  }, [view, selectedUnitId, currentUnit, currentExercise]);

  // --- Navigation ---
  const handleGoToPreface = useCallback(() => {
    setView('preface');
    window.scrollTo(0, 0);
  }, []);

  const handleGoToOfficialCourse = useCallback(() => {
    setView('official-course');
    window.scrollTo(0, 0);
  }, []);

  const handlePrefaceToUnit1 = useCallback(() => {
    setSelectedUnitId(1);
    setView('unit');
    window.scrollTo(0, 0);
  }, []);

  const handleSelectUnit = useCallback((unitId: number) => {
    setSelectedUnitId(unitId);
    setView('unit');
    window.scrollTo(0, 0);
  }, []);

  const handleBackToHome = useCallback(() => {
    setView('home');
    setSelectedUnitId(null);
    setSelectedExerciseId(null);
  }, []);

  const handleBackToUnit = useCallback(() => {
    setSelectedExerciseId(null);
    setView('unit');
    window.scrollTo(0, 0);
  }, []);

  const handleSelectExercise = useCallback((exerciseId: string) => {
    const unit = getUnitForExercise(exerciseId);
    if (unit) setSelectedUnitId(unit.id);
    setSelectedExerciseId(exerciseId);
    setView('exercise');
  }, []);

  const handlePrev = useCallback(() => {
    if (currentUnit && currentIndex > 0) {
      handleSelectExercise(currentUnit.exercises[currentIndex - 1].id);
    }
  }, [currentUnit, currentIndex, handleSelectExercise]);

  const handleNext = useCallback(() => {
    if (currentUnit && currentIndex < currentUnit.exercises.length - 1) {
      handleSelectExercise(currentUnit.exercises[currentIndex + 1].id);
    }
  }, [currentUnit, currentIndex, handleSelectExercise]);

  // --- Verification handlers ---
  const handleComplete = useCallback((exerciseId: string, userAnswer?: unknown) => {
    const updated = markCompleted(progress, exerciseId, undefined, userAnswer);
    setProgress(updated);
    saveProgress(updated);
  }, [progress]);

  const handleSubmitChoice = useCallback(async (selectedIds: string[]) => {
    if (!currentExercise?.multipleChoiceData) return;
    const result = await verifyChoice(selectedIds, currentExercise.multipleChoiceData);
    if (result.allPassed) {
      handleComplete(currentExercise.id, selectedIds);
    }
  }, [verifyChoice, currentExercise, handleComplete]);

  const handleReset = useCallback(() => {
    resetVerification();
  }, [resetVerification]);

  // --- Render ---

  if (view === 'home') {
    return (
      <LandingPage
        units={units}
        progress={progress}
        onSelectUnit={handleSelectUnit}
        onGoToPreface={handleGoToPreface}
        onGoToOfficialCourse={handleGoToOfficialCourse}
      />
    );
  }

  if (view === 'preface') {
    return (
      <PrefacePage
        onBackToHome={handleBackToHome}
        onStartLearning={handlePrefaceToUnit1}
      />
    );
  }

  if (view === 'official-course') {
    return (
      <OfficialCoursePage
        onBackToHome={handleBackToHome}
      />
    );
  }

  if (view === 'unit' && selectedUnitId !== null) {
    const unit = getUnit(selectedUnitId);
    if (!unit) return null;
    return (
      <UnitDetailPage
        unit={unit}
        progress={progress}
        onSelectExercise={handleSelectExercise}
        onBackToHome={handleBackToHome}
        totalUnits={units.length}
        onSelectUnit={handleSelectUnit}
      />
    );
  }

  if (!currentUnit || !currentExercise) return null;

  return (
    <div className="h-screen bg-gray-950 text-white flex flex-col">
      <ExerciseNav
        currentUnit={currentUnit}
        currentExercise={currentExercise}
        currentIndex={currentIndex}
        totalInUnit={currentUnit.exercises.length}
        onPrev={handlePrev}
        onNext={handleNext}
      />
      <ExerciseWorkspace
        unit={currentUnit}
        exercise={currentExercise}
        progress={progress}
        verificationResult={verificationResult}
        isVerifying={isVerifying}
        onReset={handleReset}
        onSelectExercise={handleSelectExercise}
        onBackToUnits={handleBackToUnit}
        onSubmitChoice={handleSubmitChoice}
      />
    </div>
  );
}
"
}

