import type { VerificationResult } from '@/types/exercise';

interface VerificationPanelProps {
  result: VerificationResult | null;
  isVerifying: boolean;
}

export default function VerificationPanel({
  result,
  isVerifying,
}: VerificationPanelProps) {
  if (!result && !isVerifying) return null;

  return (
    <div className="border-t border-gray-700">
      <div className="px-4 py-2 text-sm text-gray-300 flex justify-between items-center">
        <span>驗證結果</span>
        {result && (
          <span className={result.allPassed ? 'text-violet-400' : 'text-red-400'}>
            {result.allPassed ? '全部通過！' : '部分未通過'}
          </span>
        )}
      </div>

      <div className="px-4 pb-4">
        {isVerifying ? (
          <div className="text-yellow-400 text-sm">驗證中...</div>
        ) : (
          result?.details && (
            <div className="space-y-2">
              <div className="text-sm text-gray-400 mb-2">
                正確 {result.correctCount}/{result.totalCount}
              </div>
              {result.details.map((d, i) => (
                <div
                  key={i}
                  className={`rounded-lg p-3 text-sm ${
                    d.correct
                      ? 'bg-green-400/5 border border-green-400/20'
                      : 'bg-red-400/5 border border-red-400/20'
                  }`}
                >
                  <span className={d.correct ? 'text-green-400' : 'text-red-400'}>
                    {d.correct ? '正確' : '錯誤'}
                  </span>
                  {d.feedback && !d.correct && (
                    <p className="text-xs text-red-300 mt-1">{d.feedback}</p>
                  )}
                </div>
              ))}
            </div>
          )
        )}
      </div>
    </div>
  );
}
