  "content": "/**
 * Brickverse Illustration Component Library - Shared Types
 *
 * 所有插畫元件的共用介面。
 * 設計規範詳見 STYLE_GUIDE.md
 */

/** 所有插畫零件的基礎介面 */
export interface PartProps {
  /** 渲染尺寸（px），控制 SVG 的 width/height */
  size?: number;
  /** 主色 */
  color?: string;
  /** 透明度 0-1 */
  opacity?: number;
  /** 旋轉角度 */
  rotation?: number;
}

/** 裝置類元件（筆電、手機、螢幕等） */
export interface DeviceProps extends PartProps {
  /** 機身顏色 */
  bodyColor?: string;
  /** 螢幕底色 */
  screenColor?: string;
}

/** UI 模擬元件（瀏覽器視窗、卡片、Dashboard 等） */
export interface UIMockupProps extends PartProps {
  /** 強調色（按鈕、標題列等） */
  accentColor?: string;
}

/** 工作空間物件（咖啡杯、植物、書等） */
export type WorkspaceProps = PartProps;

/** 抽象概念元件（火箭、盾牌、連線等） */
export type ConceptProps = PartProps;
"
}

