  "content": "import React from 'react';
import type { UIMockupProps } from './types';

/** 瀏覽器視窗 — 前端開發、Web 相關主題 */
export const BrowserWindow: React.FC<UIMockupProps> = ({
  size = 100,
  color = '#0184ff',
  opacity = 1,
  rotation = 0,
