  "content": "import type { Metadata } from 'next';
import { BackButton } from '@/components/layout/BackButton';
import {
  BrowserWindow,
  RocketShip,
  Laptop,
  Plant,
  CoffeeMug,
} from '@/components/illustrations';

export const metadata: Metadata = {
  title: '插畫元件庫',
};

const illustrations = [
  { name: 'BrowserWindow', Component: BrowserWindow, category: 'UI 模擬', defaultColor: '#0184ff' },
  { name: 'RocketShip', Component: RocketShip, category: '抽象概念', defaultColor: '#0184ff' },
  { name: 'Laptop', Component: Laptop, category: '裝置', defaultColor: '#0184ff' },
  { name: 'Plant', Component: Plant, category: '工作空間', defaultColor: '#6BBF59' },
  { name: 'CoffeeMug', Component: CoffeeMug, category: '工作空間', defaultColor: '#E87461' },
];

const testColors = [
  { label: 'Primary', value: '#0184ff' },
  { label: 'Accent', value: '#FAD75A' },
  { label: 'Coral', value: '#E87461' },
  { label: 'Green', value: '#6BBF59' },
];

const testSizes = [60, 120, 240];

export default function IllustrationsPage() {
  return (
    <div>
      <BackButton />

      <div className="mb-12">
        <h1 className="text-4xl font-bold tracking-default mb-3">插畫元件庫</h1>
        <p className="text-lg text-text-secondary">
          模組化 SVG 插畫元件，用於部落格封面和文章視覺設計。所有元件支援自訂尺寸、顏色、透明度和旋轉。
        </p>
      </div>

      {/* Size comparison */}
      <section className="mb-16">
        <h2 className="text-2xl font-semibold mb-2">尺寸縮放測試</h2>
        <p className="text-sm text-text-secondary mb-6">
          每個元件在 60px、120px、240px 三種尺寸下的渲染效果。
        </p>

        <div className="space-y-8">
          {illustrations.map(({ name, Component, category, defaultColor }) => (
            <div key={name} className="rounded-xl border border-border p-6">
              <div className="flex items-baseline gap-3 mb-4">
                <h3 className="text-lg font-semibold">{name}</h3>
                <span className="rounded-md bg-bg-tertiary px-2 py-0.5 text-xs text-text-secondary">
                  {category}
                </span>
              </div>
              <div className="flex items-end gap-8">
                {testSizes.map((sz) => (
                  <div key={sz} className="flex flex-col items-center gap-2">
                    <div className="flex items-center justify-center rounded-lg bg-bg-secondary p-3"
                      style={{ minWidth: sz + 24, minHeight: sz + 24 }}
                    >
                      <Component size={sz} color={defaultColor} />
                    </div>
                    <span className="text-xs text-text-tertiary">{sz}px</span>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Color variation */}
      <section className="mb-16">
        <h2 className="text-2xl font-semibold mb-2">換色測試</h2>
        <p className="text-sm text-text-secondary mb-6">
          所有元件以 4 種顏色渲染，確認 color prop 正常運作。
        </p>

        <div className="grid grid-cols-2 gap-6 lg:grid-cols-4">
          {testColors.map(({ label, value }) => (
            <div key={label} className="rounded-xl border border-border p-5">
              <div className="flex items-center gap-2 mb-4">
                <div
                  className="h-4 w-4 rounded-full border border-border"
                  style={{ backgroundColor: value }}
                />
                <span className="text-sm font-medium">{label}</span>
                <span className="text-xs text-text-tertiary">{value}</span>
              </div>
              <div className="flex flex-wrap gap-4">
                {illustrations.map(({ name, Component }) => (
                  <div key={name} className="flex flex-col items-center gap-1">
                    <Component size={64} color={value} />
                    <span className="text-[10px] text-text-tertiary">{name}</span>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* All together - consistency check */}
      <section className="mb-16">
        <h2 className="text-2xl font-semibold mb-2">風格一致性檢查</h2>
        <p className="text-sm text-text-secondary mb-6">
          所有元件以統一尺寸、統一主色並排，確認整體風格和諧。
        </p>

        <div className="flex flex-wrap items-end gap-6 rounded-xl border border-border bg-bg-secondary p-8">
          {illustrations.map(({ name, Component }) => (
            <div key={name} className="flex flex-col items-center gap-2">
              <Component size={120} color="#0184ff" />
              <span className="text-xs text-text-secondary">{name}</span>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}
"
}

