  "content": "import type { Metadata } from 'next';
import { BackButton } from '@/components/layout/BackButton';
import {
  Laptop,
  DesktopMonitor,
  Smartphone,
  BrowserWindow,
  UICard,
  Terminal,
  BarChart,
  LineChart,
  PieChart,
  RocketShip,
  ConnectionNodes,
  ShieldCheck,
  CloudUpload,
  GearSettings,
  LightBulb,
  ChatBubble,
  RobotHead,
  Plant,
  CoffeeMug,
  BookStack,
} from '@/components/illustrations';

export const metadata: Metadata = {
  title: '插畫元件庫',
};

const illustrations = [
  // Devices — 各一色
  { name: 'Laptop', Component: Laptop, category: '裝置', defaultColor: '#0184ff' },
  { name: 'DesktopMonitor', Component: DesktopMonitor, category: '裝置', defaultColor: '#555555' },
  { name: 'Smartphone', Component: Smartphone, category: '裝置', defaultColor: '#E87461' },
  // UI Mockups — 分散配色
  { name: 'BrowserWindow', Component: BrowserWindow, category: 'UI 模擬', defaultColor: '#0184ff' },
  { name: 'UICard', Component: UICard, category: 'UI 模擬', defaultColor: '#6BBF59' },
  { name: 'Terminal', Component: Terminal, category: 'UI 模擬', defaultColor: '#1a1a2e' },
  // Data Visualization — 各一色
  { name: 'BarChart', Component: BarChart, category: '數據視覺化', defaultColor: '#0184ff' },
  { name: 'LineChart', Component: LineChart, category: '數據視覺化', defaultColor: '#6BBF59' },
  { name: 'PieChart', Component: PieChart, category: '數據視覺化', defaultColor: '#0184ff' },
  // Abstract Concepts — 按語意選色
  { name: 'RocketShip', Component: RocketShip, category: '抽象概念', defaultColor: '#E87461' },
  { name: 'ConnectionNodes', Component: ConnectionNodes, category: '抽象概念', defaultColor: '#0184ff' },
  { name: 'ShieldCheck', Component: ShieldCheck, category: '抽象概念', defaultColor: '#6BBF59' },
  { name: 'CloudUpload', Component: CloudUpload, category: '抽象概念', defaultColor: '#0184ff' },
  { name: 'GearSettings', Component: GearSettings, category: '抽象概念', defaultColor: '#555555' },
  { name: 'LightBulb', Component: LightBulb, category: '抽象概念', defaultColor: '#FAD75A' },
  { name: 'ChatBubble', Component: ChatBubble, category: '抽象概念', defaultColor: '#FAD75A' },
  { name: 'RobotHead', Component: RobotHead, category: '抽象概念', defaultColor: '#0184ff' },
  // Workspace — 按語意
  { name: 'Plant', Component: Plant, category: '工作空間', defaultColor: '#6BBF59' },
  { name: 'CoffeeMug', Component: CoffeeMug, category: '工作空間', defaultColor: '#E87461' },
  { name: 'BookStack', Component: BookStack, category: '工作空間', defaultColor: '#0184ff' },
];

const categories = ['裝置', 'UI 模擬', '數據視覺化', '抽象概念', '工作空間'];

export default function IllustrationsPage() {
  return (
    <div>
      <BackButton />

      <div className="mb-12">
        <h1 className="text-4xl font-bold tracking-default mb-3">插畫元件庫</h1>
        <p className="text-lg text-text-secondary">
          模組化 SVG 插畫元件，用於部落格封面和文章視覺設計。共 {illustrations.length} 個元件，支援自訂尺寸、顏色、透明度和旋轉。
        </p>
      </div>

      {/* All components gallery by category */}
      <section className="mb-16">
        <h2 className="text-2xl font-semibold mb-6">元件總覽</h2>

        {categories.map((cat) => {
          const items = illustrations.filter((i) => i.category === cat);
          return (
            <div key={cat} className="mb-10">
              <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
                <span className="rounded-md bg-bg-tertiary px-2 py-0.5 text-xs text-text-secondary">
                  {cat}
                </span>
                <span className="text-sm text-text-tertiary">{items.length} 個</span>
              </h3>
              <div className="flex flex-wrap gap-6">
                {items.map(({ name, Component, defaultColor }) => (
                  <div key={name} className="flex flex-col items-center gap-2">
                    <div className="flex items-center justify-center rounded-lg bg-bg-secondary p-4"
                      style={{ width: 144, height: 144 }}
                    >
                      <Component size={100} color={defaultColor} />
                    </div>
                    <span className="text-xs text-text-secondary">{name}</span>
                  </div>
                ))}
              </div>
            </div>
          );
        })}
      </section>

      {/* All together - consistency check */}
      <section className="mb-16">
        <h2 className="text-2xl font-semibold mb-2">風格一致性檢查</h2>
        <p className="text-sm text-text-secondary mb-6">
          所有元件以各自預設色並排，確認整體風格和諧、色調均衡。
        </p>

        <div className="flex flex-wrap items-end gap-6 rounded-xl border border-border bg-bg-secondary p-8">
          {illustrations.map(({ name, Component, defaultColor }) => (
            <div key={name} className="flex flex-col items-center gap-2">
              <Component size={100} color={defaultColor} />
              <span className="text-xs text-text-secondary">{name}</span>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}
"
}

