{
  "command": "find /Users/ruanbaiye/Documents/Brickverse/CourseFiles/AICode100/ClassPresentation -type f -name \".*\" -o -type f ! -name \".*\" | head -100",
  "description": "List all files in the ClassPresentation directory"
}

{
  "command": "ls -la \"/Users/ruanbaiye/Documents/Brickverse/CourseFiles/AICode101/ClassPersentation\" 2>&1 || ls -la \"/Users/ruanbaiye/Documents/Brickverse/CourseFiles/AICode101/ClassPresentation\" 2>&1",
  "description": "Check if directory exists with either spelling (Persentation or Presentation)"
}

{
  "command": "find /Users/ruanbaiye/Documents/Brickverse/CourseFiles/AICode100/ClassPresentation -type f -not -path \"*/node_modules/*\" -not -path \"*/.next/*\"",
  "description": "List all files excluding node_modules and .next"
}

{
  "command": "find /Users/ruanbaiye/Documents/Brickverse/CourseFiles -maxdepth 1 -type d | sort",
  "description": "Find all top-level directories in CourseFiles"
}

{
  "command": "find \"/Users/ruanbaiye/Documents/Brickverse/CourseFiles/AICode101/ClassPersentation\" -type f -name \"*.md\" -o -name \"*.json\" -o -name \"*.html\" -o -name \"*.js\" -o -name \"*.css\" -o -name \"*.txt\" | sort",
  "description": "Find all relevant files in the directory tree"
}

{
  "command": "tree -L 2 -a /Users/ruanbaiye/Documents/Brickverse/CourseFiles/",
  "description": "Get tree view of CourseFiles structure (2 levels deep)"
}

{
  "command": "tree -L 3 \"/Users/ruanbaiye/Documents/Brickverse/CourseFiles/AICode101/ClassPersentation\" 2>&1 || find \"/Users/ruanbaiye/Documents/Brickverse/CourseFiles/AICode101/ClassPersentation\" -type d | head -50",
  "description": "Get directory structure (try tree first, fallback to find)"
}

{
  "command": "find /Users/ruanbaiye/Documents/Brickverse/CourseFiles -maxdepth 2 -type f -o -type d | head -100",
  "description": "List files and directories up to 2 levels deep"
}

{
  "command": "ls -la \"/Users/ruanbaiye/Documents/Brickverse/CourseFiles/AICode101/ClassPersentation/Image\" | head -20",
  "description": "List images in the Image directory"
}

{
  "command": "ls -la \"/Users/ruanbaiye/Documents/Brickverse/CourseFiles/AICode101/ClassPersentation/Data\"",
  "description": "List data files in the Data directory"
}

{
  "command": "find /Users/ruanbaiye/Documents/Brickverse/CourseFiles/AICode101 -maxdepth 2 -name \"package.json\" -type f",
  "description": "Find all package.json files in AICode101"
}

{
  "command": "find \"/Users/ruanbaiye/Documents/Brickverse/CourseFiles/AICode101/ClassPersentation/Chapter\" -type f -name \"*.js\" -exec wc -l {} \\; | awk '{sum+=$1} END {print \"Total lines in Chapter files:\", sum}'",
  "description": "Count total lines in all Chapter files"
}

{
  "command": "find /Users/ruanbaiye/Documents/Brickverse/CourseFiles -type f \\( -name \"README.md\" -o -name \"readme.md\" \\) | grep -v node_modules",
  "description": "Find README files excluding node_modules"
}

{
  "command": "find /Users/ruanbaiye/Documents/Brickverse/CourseFiles -maxdepth 3 -type d | sort",
  "description": "List all directories up to 3 levels deep"
}

{
  "command": "for f in /Users/ruanbaiye/Documents/Brickverse/CourseFiles/AICode101/ClassPersentation/Chapter/Chapter*.js; do echo \"=== $(basename $f) ===\"; head -20 \"$f\" | grep -E \"(class|Chapter|環境|官網|儀表板|開發|資料庫|庫存|個人)\" | head -5; done",
  "description": "Get chapter titles from all Chapter files"
}

{
  "command": "find /Users/ruanbaiye/Documents/Brickverse/CourseFiles/AICode100/ClassPresentation -type f -name \"*.tsx\" -o -name \"*.ts\" -o -name \"*.json\" -o -name \"*.css\" -o -name \"*.md\" | head -100",
  "description": "Find all TypeScript, JSON, CSS, and markdown files in the ClassPresentation directory"
}

{
  "command": "tree -L 4 /Users/ruanbaiye/Documents/Brickverse/CourseFiles/AICode100/ClassPresentation 2>/dev/null || find /Users/ruanbaiye/Documents/Brickverse/CourseFiles/AICode100/ClassPresentation -print | sed -e \"s;[^/]*/;|____;g;s;____|; |;g\"",
  "description": "Generate a complete directory tree structure"
}

{
  "command": "find /Users/ruanbaiye/Documents/Brickverse/CourseFiles/AICode100/ClassPresentation -type f \\( -name \"*.tsx\" -o -name \"*.ts\" -o -name \"*.json\" -o -name \"*.css\" \\) -not -path \"*/node_modules/*\" -not -path \"*/.next/*\" | sort",
  "description": "Find all source files excluding node_modules and build artifacts"
}

{
  "command": "ls -la /Users/ruanbaiye/Documents/Brickverse/CourseFiles/AICode100/ClassPresentation/*.{ts,js,json} 2>/dev/null | grep -v node_modules",
  "description": "Check for config files in root directory"
}
