  "new_string": "// â”€â”€â”€ ä¸» Hook â”€â”€â”€
function useComponentDebug() {
  const [isDev, setIsDev] = useState(false);
  const [isActive, setIsActive] = useState(false);
  const [tooltipInfo, setTooltipInfo] = useState<TooltipInfo | null>(null);
  const [copied, setCopied] = useState(false);
  const copyTimerRef = useRef<ReturnType<typeof setTimeout> | null>(null);
  const hoveredElRef = useRef<Element | null>(null);
  const lockedElRef = useRef<Element | null>(null);

  // åµæ¸¬æ˜¯å¦ç‚º Devï¼ˆport 3000ï¼‰
  useEffect(() => {
    const port = parseInt(window.location.port) || 80;
    setIsDev(port === 3000);
  }, []);

  // æ¸…é™¤ outline
  const clearOutline = useCallback((el: Element | null) => {
    if (!el) return;
    el.style.outline = '';
    el.style.outlineOffset = '';
  }, []);

  // è¨­å®š hover outlineï¼ˆè—è‰²ï¼‰
  const setHoverOutline = useCallback((el: Element) => {
    el.style.outline = '2px solid #58a6ff';
    el.style.outlineOffset = '-2px';
  }, []);

  // è¨­å®š locked outlineï¼ˆæ©™è‰²ï¼‰
  const setLockedOutline = useCallback((el: Element) => {
    el.style.outline = '2px solid #f97316';
    el.style.outlineOffset = '-2px';
  }, []);

  // ç¶å®š mousemove
  useEffect(() => {
    if (!isActive) {
      setTooltipInfo(null);
      clearOutline(hoveredElRef.current);
      return;
    }

    const handleMouseMove = (e: MouseEvent) => {
      const target = e.target as Element;
      if (!target || target === document.body || target === document.documentElement) {
        setTooltipInfo(null);
        clearOutline(hoveredElRef.current);
        hoveredElRef.current = null;
        return;
      }

      // è·³é Debug Overlay è‡ªèº«çš„å…ƒç´ 
      if (target.closest('[data-debug-overlay]')) {
        setTooltipInfo(null);
        clearOutline(hoveredElRef.current);
        hoveredElRef.current = null;
        return;
      }

      // å¦‚æœä¸åŒæ–¼å‰ä¸€å€‹ hoverï¼Œæ¸…é™¤å‰ä¸€å€‹çš„è—æ¡†
      if (hoveredElRef.current && hoveredElRef.current !== target && hoveredElRef.current !== lockedElRef.current) {
        clearOutline(hoveredElRef.current);
      }

      // è¨­å®šæ–°çš„ hover outlineï¼ˆé™¤éæ˜¯ lockedï¼‰
      if (target !== lockedElRef.current) {
        setHoverOutline(target);
      }
      hoveredElRef.current = target;

      const path = getElementPath(target);
      setTooltipInfo({ name: path, x: e.clientX, y: e.clientY });
    };

    const handleMouseLeave = () => {
      setTooltipInfo(null);
      if (hoveredElRef.current && hoveredElRef.current !== lockedElRef.current) {
        clearOutline(hoveredElRef.current);
      }
      hoveredElRef.current = null;
    };

    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseleave', handleMouseLeave);
    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseleave', handleMouseLeave);
    };
  }, [isActive, clearOutline, setHoverOutline]);

  // ç¶å®š clickï¼ˆcapture æ¨¡å¼ï¼Œæ””æˆªæ‰€æœ‰é»æ“Šï¼‰
  useEffect(() => {
    if (!isActive) return;

    const handleClick = (e: MouseEvent) => {
      const target = e.target as Element;
      if (!target || target.closest('[data-debug-overlay]')) return;

      // æ””æˆªåŸæœ¬çš„é»æ“Šäº‹ä»¶
      e.preventDefault();
      e.stopPropagation();

      // æ¸…é™¤å‰ä¸€å€‹ locked element çš„ outline
      if (lockedElRef.current && lockedElRef.current !== target) {
        clearOutline(lockedElRef.current);
      }

      // è¨­å®šæ–°çš„ locked element
      lockedElRef.current = target;
      setLockedOutline(target);

      // è¤‡è£½è·¯å¾‘
      const path = getElementPath(target);
      copyPath(path);
    };

    // capture: true ç¢ºä¿åœ¨ä»»ä½• React onClick ä¹‹å‰æ””æˆª
    document.addEventListener('click', handleClick, { capture: true });
    return () => {
      document.removeEventListener('click', handleClick, { capture: true });
    };
  }, [isActive, clearOutline, setLockedOutline]);

  // è¤‡è£½åˆ°å‰ªè²¼ç°¿
  const copyPath = useCallback((path: string) => {
    try {
      const textarea = document.createElement('textarea');
      textarea.value = path;
      textarea.style.cssText = 'position:fixed;left:-9999px;top:-9999px;opacity:0';
      document.body.appendChild(textarea);
      textarea.select();
      document.execCommand('copy');
      document.body.removeChild(textarea);

      setCopied(true);
      if (copyTimerRef.current) clearTimeout(copyTimerRef.current);
      copyTimerRef.current = setTimeout(() => setCopied(false), 1500);
    } catch (err) {
      console.error('Copy failed:', err);
    }
  }, []);

  // é—œé–‰ debug æ™‚æ¸…é™¤æ‰€æœ‰ outline
  useEffect(() => {
    if (isActive) return;

    clearOutline(hoveredElRef.current);
    clearOutline(lockedElRef.current);
    hoveredElRef.current = null;
    lockedElRef.current = null;
  }, [isActive, clearOutline]);

  // Cleanup timer
  useEffect(() => {
    return () => {
      if (copyTimerRef.current) clearTimeout(copyTimerRef.current);
    };
  }, []);

  return { isDev, isActive, setIsActive, tooltipInfo, copied };
}"
}

