  "new_string": "// â”€â”€â”€ å·¥å…·å‡½å¼ï¼šå–å¾—å®Œæ•´ DOM è·¯å¾‘ â”€â”€â”€
function getElementPath(el: Element): string {
  const parts: string[] = [];
  let current: Element | null = el;
  let depth = 0;

  while (current && depth < 6) {
    const label = getElementLabel(current);
    parts.unshift(label);
    current = current.parentElement;
    depth++;
  }

  return parts.join(' > ');
}

// â”€â”€â”€ å·¥å…·å‡½å¼ï¼šå–å¾—å®Œæ•´çš„ Debug è³‡è¨Š â”€â”€â”€
function getDebugInfo(el: Element): { path: string; info: string } {
  const path = getElementPath(el);

  // å–å¾—æ–‡æœ¬å…§å®¹ï¼ˆæœ€å¤š 100 å­—ï¼‰
  const text = el.innerText?.trim().slice(0, 100) || '';

  // å–å¾—èªæ„ class names
  const semanticClasses = Array.from(el.classList)
    .filter(c =>
      !c.match(/^(flex|grid|p|m|w|h|text|bg|border|rounded|items|justify|gap|space|overflow|z|fixed|absolute|relative|hidden|block|inline|opacity|cursor|transition|duration|ease|hover|focus|group|min|max|col|row|top|right|bottom|left|translate|scale|rotate|shadow|font|leading|tracking|truncate|whitespace|pointer|select|ring|outline|divide|sr|animate|fill|stroke)[:-]/)
    )
    .join(', ');

  // å–å¾— ID
  const id = el.id || '';

  // å–å¾— data-* å±¬æ€§
  const dataAttrs = Array.from(el.attributes)
    .filter(attr => attr.name.startsWith('data-'))
    .map(attr => `${attr.name}="${attr.value}"`)
    .join(', ');

  // çµ„åˆè³‡è¨Š
  const parts = [
    `PATH: ${path}`,
    text ? `TEXT: ${text}` : '',
    `TAG: ${el.tagName.toLowerCase()}`,
    id ? `ID: ${id}` : '',
    semanticClasses ? `CLASSES: ${semanticClasses}` : '',
    dataAttrs ? `DATA: ${dataAttrs}` : '',
  ].filter(Boolean);

  const info = parts.join('\n');

  // åŒæ™‚è¼¸å‡ºåˆ° console
  console.log('ğŸ” Debug Info:', { path, text, tag: el.tagName.toLowerCase(), id, classes: semanticClasses, data: dataAttrs, element: el });

  return { path, info };
}"
}

