import asyncio
import json
from cdp.socket_connection import SocketConnection
from cdp.cdp_session import CDPSession


async def main():
    # Connect to Arc browser's debugging port
    connection = SocketConnection("localhost", 9222)
    session = CDPSession(connection)

    try:
        await session.connect()
        print("Connected to Arc browser via CDP")

        # Get list of targets (tabs/windows)
        targets = await session.execute(
            "Target.getTargets",
            {}
        )
        print(f"Found {len(targets['targetInfos'])} targets")

        # Create a new tab
        new_tab = await session.execute(
            "Target.createTarget",
            {
                "url": "about:blank",
                "width": 1200,
                "height": 800
            }
        )
        target_id = new_tab["targetId"]
        print(f"Created new tab: {target_id}")

        # Connect to the new tab
        ws_url = await session.execute(
            "Target.attachToTarget",
            {
                "targetId": target_id,
                "flatten": True
            }
        )
        print(f"Attaching to target: {target_id}")

        # Create a session for this target
        tab_session = CDPSession(connection, target_id)
        await tab_session.connect()

        # Enable navigation and JavaScript
        await tab_session.execute("Page.enable", {})
        await tab_session.execute("Runtime.enable", {})

        # Navigate to localhost:3000
        print("Navigating to http://localhost:3000...")
        await tab_session.execute(
            "Page.navigate",
            {"url": "http://localhost:3000"}
        )

        # Wait for page load
        await asyncio.sleep(3)
        print("Page loaded")

        # TEST: Find and click the Debug button
        print("\n=== Finding Debug Button ===")

        # Use Runtime.evaluate to find the debug button in the DOM
        debug_find = await tab_session.execute(
            "Runtime.evaluate",
            {
                "expression": """
                    const debugBtn = Array.from(document.querySelectorAll('button')).find(btn =>
                        btn.textContent.toLowerCase().includes('debug')
                    );
                    debugBtn ? {found: true, text: debugBtn.textContent} : {found: false}
                """
            }
        )
        print(f"Debug button search result: {debug_find['result']['value']}")

        if debug_find['result']['value']['found']:
            # Click the debug button
            click_result = await tab_session.execute(
                "Runtime.evaluate",
                {
                    "expression": """
                        const debugBtn = Array.from(document.querySelectorAll('button')).find(btn =>
                            btn.textContent.toLowerCase().includes('debug')
                        );
                        debugBtn.click();
                        'clicked'
                    """
                }
            )
            print(f"Debug button clicked: {click_result['result']['value']}")
            await asyncio.sleep(2)

        # TEST 1: Click on the status area
        print("\n=== TEST 1: Complete Status Area ===")

        # Find and click the status area
        test1_result = await tab_session.execute(
            "Runtime.evaluate",
            {
                "expression": """
                    // Look for the status area container (should have multiple status lines)
                    const statusArea = document.querySelector('[class*="status"]') ||
                                       document.querySelector('[class*="Status"]') ||
                                       Array.from(document.querySelectorAll('div')).find(el =>
                                           el.textContent.match(/\\d+%/) && el.textContent.match(/\\d+h/m)
                                       );
                    if (statusArea) {
                        statusArea.click();
                        'clicked_status_area'
                    } else {
                        'status_area_not_found'
                    }
                """
            }
        )
        print(f"Test 1 click result: {test1_result['result']['value']}")
        await asyncio.sleep(1)

        # Read clipboard after test 1
        clipboard1 = await tab_session.execute(
            "Runtime.evaluate",
            {
                "expression": "navigator.clipboard.readText()"
            }
        )
        print(f"Test 1 Clipboard: {clipboard1['result']['value'] if 'value' in clipboard1['result'] else clipboard1}")

        # TEST 2: Click on a single status value
        print("\n=== TEST 2: Single Status Value ===")

        test2_result = await tab_session.execute(
            "Runtime.evaluate",
            {
                "expression": """
                    // Look for a percentage or time value
                    const statusValue = Array.from(document.querySelectorAll('*')).find(el =>
                        el.textContent.match(/^\\d+%$/) || el.textContent.match(/^\\d+h\\d+m$/)
                    );
                    if (statusValue) {
                        statusValue.click();
                        'clicked_value'
                    } else {
                        'value_not_found'
                    }
                """
            }
        )
        print(f"Test 2 click result: {test2_result['result']['value']}")
        await asyncio.sleep(1)

        # Read clipboard after test 2
        clipboard2 = await tab_session.execute(
            "Runtime.evaluate",
            {
                "expression": "navigator.clipboard.readText()"
            }
        )
        print(f"Test 2 Clipboard: {clipboard2['result']['value'] if 'value' in clipboard2['result'] else clipboard2}")

        # TEST 3: Click on sidebar panel area
        print("\n=== TEST 3: Other Panel Area ===")

        test3_result = await tab_session.execute(
            "Runtime.evaluate",
            {
                "expression": """
                    // Look for sidebar or panel area
                    const sidebar = document.querySelector('[class*="sidebar"]') ||
                                   document.querySelector('[class*="Sidebar"]') ||
                                   document.querySelector('nav') ||
                                   Array.from(document.querySelectorAll('div')).find(el =>
                                       el.className.includes('left') || el.className.includes('menu')
                                   );
                    if (sidebar) {
                        const clickTarget = sidebar.querySelector('button') || sidebar.querySelector('a') || sidebar;
                        if (clickTarget) {
                            clickTarget.click();
                            'clicked_sidebar'
                        }
                    }
                    'panel_not_found'
                """
            }
        )
        print(f"Test 3 click result: {test3_result['result']['value']}")
        await asyncio.sleep(1)

        # Read clipboard after test 3
        clipboard3 = await tab_session.execute(
            "Runtime.evaluate",
            {
                "expression": "navigator.clipboard.readText()"
            }
        )
        print(f"Test 3 Clipboard: {clipboard3['result']['value'] if 'value' in clipboard3['result'] else clipboard3}")

        # Summary
        print("\n=== TEST SUMMARY ===")
        print(f"Test 1 Clipboard: {clipboard1['result']['value'] if 'value' in clipboard1['result'] else 'N/A'}")
        print(f"Test 2 Clipboard: {clipboard2['result']['value'] if 'value' in clipboard2['result'] else 'N/A'}")
        print(f"Test 3 Clipboard: {clipboard3['result']['value'] if 'value' in clipboard3['result'] else 'N/A'}")

    except Exception as e:
        print(f"Error: {e}")
        import traceback
        traceback.print_exc()
    finally:
        await session.disconnect()


if __name__ == "__main__":
    asyncio.run(main())
"
}

