        data = json.loads(response.read().decode())
        return data
    except Exception as e:
        print(f"Error getting debugger URLs: {e}")
        return []


def get_websocket_url():
    """Get websocket URL for a new tab"""
    try:
        targets = get_debugger_urls()
        if targets:
            for target in targets:
                if 'webSocketDebuggerUrl' in target:
                    print(f"Found target: {target.get('title', 'Unknown')}")
                    return target['webSocketDebuggerUrl']
    except Exception as e:
        print(f"Error: {e}")
    return None


class CDPClient:
    def __init__(self, ws_url):
        self.ws_url = ws_url
        self.ws = None
        self.msg_id = 0
        self.responses = {}

    def connect(self):
        """Connect to CDP websocket"""
        try:
            self.ws = websocket.create_connection(self.ws_url, timeout=10)
            print(f"Connected to {self.ws_url}")
            return True
        except Exception as e:
            print(f"Connection error: {e}")
            return False

    def send_command(self, method, params=None, wait=True):
        """Send a CDP command"""
        self.msg_id += 1
        msg = {
            "id": self.msg_id,
            "method": method,
            "params": params or {}
        }

        try:
            self.ws.send(json.dumps(msg))
            print(f"Sent: {method}")

            if wait:
                # Wait for response
                while True:
                    response = self.ws.recv()
                    data = json.loads(response)

                    if 'id' in data and data['id'] == self.msg_id:
                        return data.get('result', data.get('error'))
                    # Store other messages
                    time.sleep(0.1)
        except Exception as e:
            print(f"Send error: {e}")
            return None

    def execute_javascript(self, code):
        """Execute JavaScript in the page"""
        return self.send_command(
            "Runtime.evaluate",
            {"expression": code, "returnByValue": True}
        )

    def navigate(self, url):
        """Navigate to URL"""
        return self.send_command(
            "Page.navigate",
            {"url": url}
        )

    def close(self):
        """Close connection"""
        if self.ws:
            self.ws.close()


def main():
