                    reject(e);
                }
            });
        });

        req.on('error', (e) => {
            reject(e);
        });
        req.end();
    });
}

// CDP Client class
class CDPClient {
    constructor(wsUrl) {
        this.wsUrl = wsUrl;
        this.ws = null;
        this.msgId = 0;
        this.pendingResponses = new Map();
    }

    async connect() {
        return new Promise((resolve, reject) => {
            try {
                this.ws = new WebSocket(this.wsUrl);

                this.ws.on('open', () => {
                    console.log('Connected to CDP');
                    resolve(true);
                });

                this.ws.on('message', (data) => {
                    const msg = JSON.parse(data);
                    if (msg.id && this.pendingResponses.has(msg.id)) {
                        const resolver = this.pendingResponses.get(msg.id);
                        this.pendingResponses.delete(msg.id);
                        resolver(msg);
                    }
                });

                this.ws.on('error', (err) => {
                    reject(err);
                });
            } catch (e) {
                reject(e);
            }
        });
    }

    async sendCommand(method, params = {}) {
        return new Promise((resolve, reject) => {
            this.msgId++;
            const msg = {
                id: this.msgId,
                method: method,
                params: params
            };

            const timeout = setTimeout(() => {
                this.pendingResponses.delete(this.msgId);
                reject(new Error(`Command ${method} timed out`));
            }, 10000);

            this.pendingResponses.set(this.msgId, (response) => {
                clearTimeout(timeout);
                resolve(response.result || response.error);
            });

            this.ws.send(JSON.stringify(msg));
        });
    }

    async executeJavaScript(code) {
        const result = await this.sendCommand('Runtime.evaluate', {
            expression: code,
            returnByValue: true
        });
